import React from "react";
import "./index.css";
import "@chakra-ui/react"
import { Box,Image,Center,Text} from "@chakra-ui/react";
import Typewriter from 'typewriter-effect';



function Splash(){
return(<> 
<div className="bg">
  <Center py={'190'}>
  <Box >
<Center>
  <Image className="image" alt=""/>
  </Center>
  
  <Text  className="text"  textAlign={'center'} py={'15'} color={'brown'} fontSize={'larger'} fontStyle={'italic'} fontFamily={"cursive"}  textShadow="1px 1px #ff0000" m="6">
  <Typewriter
  onInit={(typewriter) => {
    typewriter.typeString('An initiative by GGCT students to tackle Covid-19').pause(5).start();
  }}
/>
     </Text>
</Box>
</Center>
</div>
  </>
)
}
export default Splash;
