import react from "react";
import "./donor.css"
import CoWithYou from "./CoWithYou Logo.png"
const Donor=()=>{
    const [pro,setProduct]=useState({
        Pname:null,
        PMD:null,
        PED:null,
        Contact:null,
        PDescription:null
    })
    let name,value
    const handleInputs=(e)=>{
        name=e.target.name;
        value=e.target.value
        if(!name||!value)
        window.alert("ERROR")
        else
        setProduct({...user,[name]:value})
    }
    const PostData=async (e)=>{
        e.preventDefault()
        const {PName,PMD,PED,Contact,PDescription}=pro;
        const res=await fetch("/Donor",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                PName,PMD,PED,Contact,PDescription
            })
        })
        const data=await res.json();
        if(res.status===400||!data){
            window.alert("Failed to Upload.Check the details you have filled and try again");
            console.log("Failed to Upload.Check the details you have filled and try again")
        }
        else{
            console.log(res.status)
            window.alert("Successful registration");
            console.log("Successful registration")
            console.log(history)
        }
    }
    return(
        <>
        <div className="container" id="container">
        <div id="header">
        <img className="Logo1"src={CoWithYou}></img>Namsthey SAVES your life... Shake hand SHAKES your life... 
        <img className="Logo2" src={CoWithYou}></img></div>
        <div className="form" id="form">
        <div id="head">
        Upload product details on CoWithYou
        </div>
                <form method="POST" className="form2" id="form2">
                <div className="Details">
                <div id="PName">
                PNAME:-
                    <input type="text" name="PName"  placeholder="Enter product's name" value={pro.Pname}onChange={handleInputs}/>
                    </div>
                    <div id="email">
                    EMAIL:-
                    <input type="email" name="email" placeholder="Enter your email" value={user.email}onChange={handleInputs}/>
                    </div>
                    <div id="phone" >
                    PHONE:-
                    <input type="Number" name="phone"  placeholder="Enter your phone" value={user.phone}onChange={handleInputs}/>
                    </div>
                    <div id="PASSWORD">
                    PASSWORD
                    <input type="text" name="password" id="password" placeholder="Enter your password" value={user.password}onChange={handleInputs}/>
                    </div>
                    <div id="cpassword">
                    CONFIRM PASSWORD
                    <input type="text" name="cpassword" placeholder="Confirm your password" value={user.cpassword} onChange={handleInputs}/>
                    </div>
                    <div id="Submit">
                    <input type="submit" name="signup" id="signup" className="form-submit" value="REGISTER" onClick={PostData}/>
                    </div>
                    <a href="../Login/Login.jsx">Login</a>
                    </div>
                </form>
        </div>
        </div>
        </>
    )
}
export default Donor
