import react,{useState,useEffect} from "react";
import "./SignUp.css"
import CoWithYou from "./CoWithYou Logo.png"
import {useHistory} from "react-router-dom"
const SignUp=()=>{
    var history=useHistory()
    const [user,setUser]=useState({
        name:"",
        email:"",
        phone:0,
        password:"",
        cpassword:""
    })
    let name,value
    const handleInputs=(e)=>{
        name=e.target.name;
        value=e.target.value
        setUser({...user,[name]:value})
    }
    const PostData=async (e)=>{
        e.preventDefault()
        const {name,email,phone,password,cpassword}=user;
        console.log("Hello")
        const res=await fetch("/Register",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,email,phone,password,cpassword
            })
        })
        const data=await res.json();
        if(res.status===422||!data){
            window.alert("Invalid registration");
            console.log("Invalid registration")
        }
        else{
            window.alert("Successful registration");
            console.log("Successful registration")
            console.log(history)
            history.push("/Login")
        }
    }
    return(
        <>
        <div className="container" id="container">
        <div id="header">
        <img className="Logo1"src={CoWithYou}></img>Namsthey SAVES your life... Shake hand SHAKES your life... 
        <img className="Logo2" src={CoWithYou}></img></div>
        <div className="form" id="form">
        <section id="signup" style={{display:"none"}}className="signup">
            <div className="container mt-5">
                <h3>Form</h3>
                <form method="POST" className="registration-form" id="registration-form">
                    <input type="text" name="name" id="name" placeholder="Enter your name" value={user.name}onChange={handleInputs}/>
                    <input type="email" name="email" id="email" placeholder="Enter your email" value={user.email}onChange={handleInputs}/>
                    <input type="Number" name="phone" id="phone" placeholder="Enter your phone" value={user.phone}onChange={handleInputs}/>
                    <input type="text" name="password" id="password" placeholder="Enter your password" value={user.password}onChange={handleInputs}/>
                    <input type="text" name="cpassword" id="cpassword" placeholder="Confirm your password" value={user.cpassword} onChange={handleInputs}/>
                    <input type="submit" name="signup" id="signup" className="form-submit" value="register" onClick={PostData}/>
                </form>
            </div>
        </section>
        </div>
</div>
        </>
    )
}
export default SignUp
