import React,{useState,useEffect} from "react";
import "./Splash.css";
import "@chakra-ui/react"
import { Box,Image,Center,Text} from "@chakra-ui/react";
import Typewriter from 'typewriter-effect';



function Splash(){
  const TimeOut=()=>{useEffect(()=>{document.getElementById("bg").style.display="none"})}
  setTimeout(TimeOut,2000)
return(<> 
<div className="bg" id="bg">
  <div className="image" alt=""/>
  
  <Text  className="text"  textAlign={'center'} py={'15'} color={'brown'} height={"auto"} width={"auto"}fontSize={"40px"} fontStyle={'italic'} fontFamily={"cursive"}  textShadow="1px 1px #ff0000" m="6">
  <Typewriter
  onInit={(typewriter) => {
    typewriter.typeString('An initiative by GGCT students').pause(5).start();
  }}
/>
     </Text>
</div>

  </>
)


}
export default Splash;
