import react,{useState} from "react";
import "./login.css";
import {Text,Stack,Flex,Box,Image,Heading,FormControl,Button,FormLabel, Icon,InputLeftElement,InputRightElement,InputGroup,Input,Link,Checkbox} from "@chakra-ui/react";
import { toast, ToastContainer } from "react-toastify";
function Login()
{var flag;

 function validate(e) {
   let name=e.target.value;
   if(name.length==0)
{flag=0;}
else{flag=1;}
}
function notify(){
  if(flag==1)
  {toast("Login successfull",{position:"top-center"});}
}
const [email,setEmail]=useState("");
const [password,setPassword]=useState("")

const LoginUser=async (e)=>{
  e.preventDefault()
  const res=await fetch("/Login",{
    method:"POST",
    headers:{
      "Content-Type":"application/json"
    },
    body:JSON.stringify({
email,password
    })
  })
  const data=res.json();
  if(!data||res.status===400){
    window.alert("Invalid credentials")
  }
  else{
    window.alert("Succesfully LoggedIn")
  }
}
  
   return(<>

    
     
     <Text  fontSize="smaller" align={'auto'} fontFamily={'cursive'}>FIGHT TOGETHER AGAINST COVID-19</Text>
     <hr></hr>
      <Flex minH={'70vh'} align={'center'} justifyContent={'center'} wrap={'wrap'}>  
      <Box  className="container">
        <Stack  align={'center'}  padding={'5'} spacing={'6'}>
          <Heading fontSize={'5xl'}>SIGN IN </Heading>
          <form method="POST" action="submit" >
          <FormControl isRequired> 
            <FormLabel>E-mail</FormLabel>
              <InputGroup size="sm">
              <Input id="email" type="email" placeholder="Enter your E-mail"  value={email} onChange={(e)=>setEmail(e.target.value)}mt={'2'} />
        </InputGroup></FormControl>
        <FormControl isRequired> 
            <FormLabel>password</FormLabel>
              <InputGroup size="sm"> 
              <Input id="password" value={password} onChange={(e)=>setPassword(e.target.value)}type="password" placeholder="Enter your password"  mt={'2'} />
        </InputGroup></FormControl>
        <Stack spacing={10} direction="row">
        <Checkbox  size="lg"iconColor={'black'} iconSize={'0.8rem'} defaultIsChecked> Remember me</Checkbox></Stack>
        <Link color={'blue'} fontSize={'2xl'} href="\components\Forgetpassword.jsx">Forgot your password?</Link>
        <Stack justifyContent="center" >
            <Button className="button" type={'submit'} onClick={LoginUser} size="larger" mt={'8'} >
              Sign In
            </Button></Stack>
            <ToastContainer />
          </form>
         <Text fontSize="large">or</Text>
         <Link textColor={'blue'}  mt={'15'} href="\components\Registration.jsx" > Register now</Link>
          </Stack>    
  </Box>
   </Flex>

  </>
);}
export default Login;
